mapper.select:
SELECT C.CONSULT_NO ,C.TITLE ,C.CONTENT ,C.DT ,C.STATE_CD ,C.GUIDE_ID ,C.JOB_CD ,C.ENNC ,C.BEGIN_DATE ,C.END_DATE ,FUN_CODENAME('D', G.DUTY) AS DUTY_NAME ,FUN_CODENAME('R', G.CAREER) AS CAREER ,FUN_CODENAME('J', C.JOB_CD) AS JOB_NAME FROM CONSULT C, GUIDE G C.GUIDE_ID=G.GUIDE_ID
AND STATE_CD =#{stateCd}
AND C.GUIDE_ID=#{guideId} ORDER BY CONSULT_NO DESC

SELECT C.CONSULT_NO ,C.TITLE ,C.CONTENT ,C.DT ,C.STATE_CD ,C.GUIDE_ID ,C.JOB_CD ,C.ENNC ,C.BEGIN_DATE ,C.END_DATE ,FUN_CODENAME('D', G.DUTY) AS DUTY_NAME ,FUN_CODENAME('R', G.CAREER) AS CAREER ,FUN_CODENAME('J', C.JOB_CD) AS JOB_NAME FROM CONSULT C, GUIDE G C.GUIDE_ID=G.GUIDE_ID
AND JOB_CD = #{jobCd}
AND ENNC =#{ennc}
ORDER BY CONSULT_NO DESC

select chat_link
from consult_chat
where consult_no = #{consultNo}
