mapper.select:
SELECT C.CONSULT_NO ,C.TITLE ,C.CONTENT ,C.DT ,C.STATE_CD ,C.GUIDE_ID ,C.JOB_CD ,C.ENNC ,C.BEGIN_DATE ,C.END_DATE ,CM.CD_NAME ,CASE WHEN G.CAREER= '1' THEN '신입(1년 이하)' WHEN G.CAREER= '2' THEN '주니어(1년 ~ 3년)' WHEN G.CAREER= '3' THEN '미들(4년 ~ 8년)' WHEN G.CAREER= '4' THEN '시니어(9년 이상)' WHEN G.CAREER= '5' THEN 'Lead레벨(15년 이상)' ELSE ' ' END AS CAREER FROM CONSULT C, GUIDE G, CMMNCD CM
C.GUIDE_ID=G.GUIDE_ID AND G.DUTY= CM.CD_NO AND CM.CD_INI='D'
AND JOB_CD = #{jobCd}
and ennc =#{ennc}
ORDER BY CONSULT_NO DESC
