mapper.update:
update member

	nick = #{nick},
	name = #{name},
	email = #{email}

where member_id = #{memberId}

mapper.select:
SELECT MEMBER_ID, 
       RPAD(SUBSTR(MEMBER_ID,1,2),LENGTH(MEMBER_ID),'*') AS MEMBER_ID_STAR  
FROM MEMBER WHERE NAME =#{name} AND EMAIL=#{email}

mapper.update:
UPDATE MEMBER 
SET PW = #{pw}
WHERE MEMBER_ID = #{memberId}
